import React, { useState } from 'react';
import HeaderNav from './components/Header';


const Login = ()=>{

    // let [FormData, setData] = useState({
    //     number: '',
    //     password: ''
    // })
    

    let [inputPasField, setBehaviorOnPasswordInput] = useState({
        classElements: "btn btn-outline-secondary disabled",
        valueElements: ''
    })

    let [inputNumField, setBehaviorOnNumberInput] = useState()

    function inputPasHandler(e){
        if(e.target.value!==''){
            setBehaviorOnPasswordInput(prev=>({...prev, classElements: "btn btn-outline-primary"}))
        }
        else {
            setBehaviorOnPasswordInput(prev=>({...prev, classElements: "btn btn-outline-secondary disabled"}))
        }
    }

    function changeDataHandlerOnPassword(e){
        setBehaviorOnPasswordInput(prev=>({ ...prev, valueElements: e.target.value}))
    }

    // function changeDataHandler(e){
    //     let {name, value} = e.target
    //     setData(prev=>({ ...prev, [name]:value}))
    
    // }



    return(
       <>
       <HeaderNav></HeaderNav>
        <main>
                <div className="container-xl mt-5">
                    
                    <div className="login-form d-flex flex-column align-items-center justify-content-center" style={{height: '70vh'}} id="divForm">
                        <h2 className="myh2" style={{marginBottom: '2rem'}}>Вход в систему</h2>
                        
                        <form className="g-3" action="/" method="post" id="authorize_form" >
                            <div className="mb-3">
                                
                                <input type="tel" className="form-control inputNumField" id="phoneNumber" placeholder="Номер телефона" name="number" value={FormData.num} onInput={inputNumField} />
                            </div>

                            <div className="mb-3 input-group">
                                <input type="password" onChange={changeDataHandlerOnPassword} className="form-control" id="pas" placeholder="Пароль" name="password" value={FormData.pas} onInput={inputPasHandler}/>
                                
                                <button id="showPsw" className={inputPasField}type="button">Показать</button>
                    
                            </div>
                            <div className="mb-3">
                                <input type="submit" className="btn btn-primary disabled mb-3" id="authorize" value="Авторизоваться" style={{width: '100%'}}/>
                            </div>
                        </form>
                    </div>
                </div>
            </main>
       </>
    );
}
 
export default Login;
