import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import api from "../interceptor";


const Teachers = ()=>{
    useEffect(()=>{
        api.get(`/teacher`, bodyForm).then(data=>{
            setShowError("")
            //console.log(data.data)
            localStorage.setItem("token", JSON.stringify(data.data.token))
            //window.location.href = `/teacher/${data.data.teacher_id}`
            navigate(`/${data.data.role}/${data.data.teacher_id}`)
        }).catch(error=>{
            setShowError(error.response.data)
            //console.log(error.response.data)
        })
    })
    return (
        <div className="container">
            <div className="row">
                <h1 className="mt-3">Добро пожаловать</h1>
                <p>У вас есть баллов</p>
                

                <div className="list-group">
                    <Link to="/teacher/checkpoints/#{id}" className="list-group-item">Баллы</Link>
                    <Link to="/teacher/aplication_for_olimpiad/#{id}" className="list-group-item">Заявка на олимпиаду</Link>
                    <Link to="/teacher/aplication_for_free_attendance/#{id}" className="list-group-item">Заявка на свободное посещение ученику</Link>
                    <Link to="/teacher/subject_report/#{id}" className="list-group-item">Отчет предметника</Link>
                    <Link to="/teacher/homeroom_teacher_report/#{id}" className="list-group-item">Отчет классного руководителя</Link>
                </div>
            </div>
        </div>
    )
}
export default Teachers
