import React, { useState } from 'react';
import HeaderNav from './components/Header';


const Login = ()=>{
    let [FormData, setData] = useState({
        num: '',
        pas: ''
    })
    

    let [inputPasField, setBehaviorOnPasswordInput] = useState("btn btn-outline-secondary disabled")
    
    function inputPasHandler(e){
        if(e.target.value!=''){
            setBehaviorOnPasswordInput("btn-outline-primary")
        }
        else {
            setBehaviorOnPasswordInput("btn btn-outline-secondary disabled")
        }
    }

    function changeDataHandler(e){
        let {valueName, value} = e.target
        setData(prev=>({ ...prev, [valueName]:value}))
    }



    return(
       <>
       <HeaderNav></HeaderNav>
        <main>
                <div className="container-xl mt-5">
                    
                    <div className="login-form d-flex flex-column align-items-center justify-content-center" style={{height: '70vh'}} id="divForm">
                        <h2 className="myh2" style={{marginBottom: '2rem'}}>Вход в систему</h2>
                        
                        <form className="g-3" action="/" method="post" id="authorize_form" onSubmit={submitFormHandler}>
                            <div className="mb-3">
                                
                                <input type="tel" onChange={changeDataHandler} className="form-control " id="phoneNumber" placeholder="Номер телефона" name="number" value={FormData.num} />
                            </div>

                            <div className="mb-3 input-group">
                                <input type="password" onChange={changeDataHandler} className="form-control" id="pas" placeholder="Пароль" name="password" value={FormData.pas} onInput={inputPasHandler}/>
                                
                                <button id="showPsw" className={inputPasField}type="button">Показать</button>
                    
                            </div>
                            <div className="mb-3">
                                <input type="submit" className="btn btn-primary disabled mb-3" id="authorize" value="Авторизоваться" style={{width: '100%'}}/>
                            </div>
                        </form>
                    </div>
                </div>
            </main>
       </>
    );
}
 
export default Login;
