import React, { useEffect, useState } from 'react'
import HeaderNav from './components/Header'
import useInterceptors from "../hooks/UseInterceptor";
import { useNavigate } from 'react-router-dom';
import {PlusCircle, MinusCircle, Search} from 'react-feather';

function Checkpoints() {
   let [usrId, setUsrId] = useState()
   let [isValid, setIsValid] = useState(false)
   const navigateTo = useNavigate()
   const axiosInterceptors = useInterceptors()
    useEffect(()=>{
        const fetchTeachersData = async ()=>{
            await axiosInterceptors.post("/check_page")
                .then(data=>{
                    if (data.data) {
                        setIsValid(true)
                    }
                })
                .catch((error)=>{
                    navigateTo("/error_page?statusCode=500&errorMessage="+error)
                })
        }
        fetchTeachersData()
        setUsrId(localStorage.getItem('teacherId'))
    },[axiosInterceptors, navigateTo])

    let [searchElement, setSearchElement] = useState('')
    
    let [respondForSearch, setRespondForSearch] = useState({// TUT NUJNO BUTET ZADAT PUSTOI MASSIV A V SETrESPOND ZADAVAT MASSIV OBIEKTOV 
        student_id: '',
        studentName: '',
        email: '',
        graduation: '',
        phone: '',
        checkpoints: ''
    })
    let [messageIfErr, setMessageIfErr] = useState('')

    async function findStudents(e){
        e.preventDefault()
        setSearchElement(e.target.value)
        let bodyForm = {
            search: searchElement
        }
        await axiosInterceptors.post("/search_for_checkpoints", bodyForm).then(data =>{//TUT IDET VOZVRAT MASSIVA, TEBE NUJNO PRIDUMAT KAK ETO OBOITI
            console.log(data.data)
            if(data.data){
                setRespondForSearch(prev=>({...prev,
                    student_id: data.data.id_student,
                    studentName: `${data.data.name} ${data.data.surname} ${data.data.patronymic}`,
                    email: data.data.email,
                    graduation: data.data.graduation,
                    phone: data.data.phone,
                    checkpoints: data.data.checkpoints
                }))
            }
            else{
                setMessageIfErr(data.response.data)
            }

        })
    }
    return isValid ? (
        <>
        <HeaderNav></HeaderNav>
            <div className="container ">

                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item"><a href={`/teacher/${usrId}`}>Домашняя</a></li>
                        <li className="breadcrumb-item active" aria-current="page">Баллы</li>
                    </ol>
                </nav>

                <div className="row">
                    <div className="align-self-start input-group mb-3">
                        <input 
                            type="text" 
                            className="form-control border border-primary"
                            placeholder='Введите ФИО ученика'
                            name='search'
                            onInput={findStudents} 
                        />
                        <span className="input-group-text btn btn-outline-primary" id="inputGroup-sizing-default"><span className="textInBtn">Найти</span> <Search/></span>
                    </div> 
                </div>
                {respondForSearch.studentName ? 
                    <ul className="list-group">
                        <li className="list-group-item d-flex justify-content-between align-items-center">
                            <span className="studentName">{respondForSearch.studentName}</span>
                            <span className="studentEmail" style={{color: '#6c757d' }}>{respondForSearch.email}</span>
                            <span className="studentPhone" style={{color: '#6c757d' }}>{respondForSearch.phone}</span>
                            <span style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
                                <span className="btn btn-outline-primary m-1" id={'add_'+respondForSearch.student_id}><span className="textInBtn">Добавить</span> <PlusCircle/></span>
                                <span className="btn btn-outline-primary m-1" id={'subtract_'+ respondForSearch.student_id}><span className="textInBtn">Убрать</span> <MinusCircle/></span>
                                <span className="badge bg-primary rounded-pill m-1" >{respondForSearch.checkpoints}</span>
                            </span>
                        </li> 
                    </ul>
                    : null
                }              
            </div>       
        </>
    ) : null
}

export default Checkpoints
