import axios from "axios"
//import dotenv from "dotenv"
import {useNavigate} from "react-router-dom"

const api = axios.create({
    baseURL: "http://localhost:5000/rili_api/"
})

// Добавление интерцептора для каждого запроса
api.interceptors.request.use(config => {
    const token =  localStorage.getItem('token')// Ваш токен аутентификации
    config.headers.Authorization = `Bearer ${token}` // Добавление заголовка Authorization
    return config
  }, error => {
    return Promise.reject(error)
  })

const navigateTo = useNavigate()

api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response) {
      // Ошибка пришла от сервера (например, статус код не 2xx)
      //console.error('Ошибка от сервера:', error.response.data);
      navigateTo("/error_page", {state: {stsCode: 502, errMessage: `Ошибка от сервера: ${error.response.data}`}})
        // Можно добавить здесь логику обработки конкретных ошибок, если нужно

      if (error.response.status === 401) {
        // Если статус код 401 (несанкционированный доступ), то можно выполнить логаут пользователя
        localStorage.clear(); // Очищаем хранилище токена или других авторизационных данных
        if(window.location.pathname !== "/"){
          //window.location.href = "http://localhost:3000"
          navigateTo("/")
        }
         
      }
    } else if (error.request) {
      // Ошибка связанная с запросом (например, не удалось доставить запрос)
      //console.error('Ошибка запроса:', error.request);
      navigateTo("/error_page", {state: {stsCode: 400, errMessage: `Ошибка запроса: ${error.request}`}})
    } else {
      // Другие ошибки
      //console.error('Ошибка:', error.message);
      navigateTo("/error_page", {state: {stsCode: 400, errMessage: `Ошибка: ${error.message}`}})
    }
    
    return Promise.reject(error);
  }
)

  export default api
