import React, { useEffect, useState } from 'react'
import useInterceptors from '../../hooks/UseInterceptor'
import {ArrowRightCircle} from 'react-feather';
import HeaderNav from '../components/Header';
import {Link} from 'react-router-dom'

function OlimpioadApplication() {
    let [olimpiadName, setOlimpiadName] = useState('')
    let [usrId, setUsrId] = useState()
    let [isValid, setIsValid] = useState(false)
    const axiosInterceptors = useInterceptors()

    useEffect(()=>{
        const fetchTeachersData = async ()=>{
            await axiosInterceptors.post("/check_page")
                .then(data=>{
                    if (data.data) {
                        setIsValid(true)
                    }
                    else{
                        data.response ? console.log(data.response.data) : console.log("Something realy bad happend to server!!!")
                    }
                }) 
        }
        fetchTeachersData()        
        setUsrId(localStorage.getItem('teacherId'))
    },[axiosInterceptors])
    

    function saveName(e){
        localStorage.setItem("olimpiadName", olimpiadName)
    }
    return isValid ? (
        <>
        <HeaderNav></HeaderNav>
            <div className="container ">
            <div className="row">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item"><a href={`/teacher/${usrId}`}>Домашняя</a></li>
                        <li className="breadcrumb-item active" aria-current="page">Заявка на олимпиаду</li>
                    </ol>
                </nav>
                
                <ul className="nav nav-tabs">
                    <li className="nav-item">
                        <Link className="nav-link active" aria-current="page" to="#">Active</Link>
                    </li>
                    <li className="nav-item">
                        <Link className="nav-link" to="#">Link</Link>
                    </li>
                    <li className="nav-item">
                        <Link className="nav-link" to="#">Link</Link>
                    </li>
                    
                </ul>
                
                    <div className="align-self-start input-group mb-3">
                        <input 
                            type="text" 
                            className="form-control border border-primary"
                            placeholder='Введите цель и названи мероприятия'
                            name='olimpiad-name'
                            value={olimpiadName}
                            onChange={(e)=>setOlimpiadName(e.target.value)}
                        />
                        <span className="input-group-text btn btn-outline-primary" id="inputGroup-sizing-default"><span className="textInBtn" onClick={saveName}>Дальше</span> <ArrowRightCircle/></span>
                    </div> 
                </div>
                             
            </div>       
        </>
    ) : null
}

export default OlimpioadApplication
