import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../interceptor";


const Teachers = ()=>{
    let [teacherData, setTeacherData] = useState({
        teacherName: '',
        checkpoints: '',
        teacher_id: ''
    })
    useEffect(()=>{
        const fetchTeachersData = async ()=>{
            await api.post(`${window.location.pathname}`).then(data=>{
                console.log(data)
                if(data.length!==0 && data.data){
                    setTeacherData(prev=>({...prev, teacherName: data.data.name, checkpoints: data.data.checkpoints, teacher_id: data.data.teacher_id}))
                }
                
            }).catch(error=>{
                console.log(error.response)
            })
        }
        fetchTeachersData()
    }, [])
    return (
        <div className="container">
            <div className="row">
                <h1 className="mt-3">Добро пожаловать {teacherData.teacherName}</h1>
                <p>У вас есть баллов {teacherData.checkpoints}</p>
                

                <div className="list-group">
                    <Link to="/teacher/checkpoints/" className="list-group-item">Баллы</Link>
                    <Link to="/teacher/aplication_for_olimpiad/" className="list-group-item">Заявка на олимпиаду</Link>
                    <Link to="/teacher/aplication_for_free_attendance/" className="list-group-item">Заявка на свободное посещение ученику</Link>
                    <Link to="/teacher/subject_report/" className="list-group-item">Отчет предметника</Link>
                    <Link to="/teacher/homeroom_teacher_report/" className="list-group-item">Отчет классного руководителя</Link>
                </div>
            </div>
        </div>
    )
}
export default Teachers
