import React, { useEffect, useState } from 'react';
import HeaderNav from './components/Header';


const Login = ()=>{

    let [inputPasField, passwordSetBehaviorOnInput] = useState({
        classElements: "btn btn-outline-secondary disabled",
        valueElements: "",
        typeField: "password"
    })

    

    function changeDataHandlerOnPassword(e){
        if(e.target.value!==''){
            passwordSetBehaviorOnInput(prev=>({...prev, classElements: "btn btn-outline-primary", valueElements: e.target.value}))//nujno budet nedopuskat vvod probelov i drugih znakov
        }
        else {
            passwordSetBehaviorOnInput(prev=>({...prev, classElements: "btn btn-outline-secondary disabled", valueElements:""}))
        
        }
        
    }


    let [inputNumField, numberSetBehaviorOnInput] = useState({
        classElements: "form-control",
        valueElements: "",
        errorClass: "",
        errorText: ""
    })


    function changeDataHandlerOnNumber(e){
        if(e.target.value.search(/^[\+\d\s]+$/) === -1 && e.target.value !== ""){
            numberSetBehaviorOnInput(prev=>({
                ...prev,
                valueElements: e.target.value.trim(),
                classElements: "form-control is-invalid",
                errorClass: "invalid-feedback",
                errorText: "Вы вводите недопустимое значение"
            }))
        }
        else if(e.target.value === ""){
            numberSetBehaviorOnInput(prev=>({
                ...prev,
                valueElements: "",
                classElements: "form-control",
                errorClass: "",
                errorText: ""
            }))
        }
        else{
            numberSetBehaviorOnInput(prev=>({
                ...prev,
                valueElements: e.target.value,
                classElements: "form-control",
                errorClass: "",
                errorText: ""
            }))
        }
        
    }

    let [btnState, setBtnState] = useState("btn btn-primary disabled mb-3")
    useEffect(()=>{
        if(inputPasField.valueElements!=="" && inputNumField.valueElements!==""){
            setBtnState("btn btn-primary mb-3")
        }
        else{
            setBtnState("btn btn-primary disabled mb-3")
        }
    }, [inputPasField, inputNumField])


    function showPsw(e){

    }

    return(
       <>
       <HeaderNav></HeaderNav>
        <main>
                <div className="container-xl mt-5">
                    
                    <div className="login-form d-flex flex-column align-items-center justify-content-center" style={{height: '70vh'}} id="divForm">
                        <h2 className="myh2" style={{marginBottom: '2rem'}}>Вход в систему</h2>
                        
                        <form className="g-3" action="/" method="post" id="authorize_form" >
                            <div className="mb-3">
                                
                                <input type="tel" className={inputNumField.classElements} id="phoneNumber" placeholder="Номер телефона" name="number" value={inputNumField.valueElements} onInput={changeDataHandlerOnNumber} />

                                {inputNumField.errorClass && <div className={inputNumField.errorClass}>{inputNumField.errorText}</div>}
                            </div>


                            <div className="mb-3 input-group">
                                <input type="password" onInput={changeDataHandlerOnPassword} className="form-control" id="pas" placeholder="Пароль" name="password" value={inputPasField.valueElements}/>
                                


                                <button id="showPsw" className={inputPasField.classElements}type="button" onClick={showPsw}>Показать</button>
                    
                            </div>
                            <div className="mb-3">
                                <input type="submit" className={btnState} id="authorize" value="Авторизоваться" style={{width: '100%'}}/>
                            </div>
                        </form>
                    </div>
                </div>
            </main>
       </>
    );
}
 
export default Login;
